import { createApp } from 'vue';
import App from './App.vue';
import { createRouter, createWebHistory } from 'vue-router';
import HomePage from './components/HomePage.vue';
import LoginPage from './components/LoginPage.vue';
import store from './store';  // Импортируем store.js
import ProfilePage from './components/ProfilePage.vue';
import UserManagement from './components/UserManagement.vue';
import UserForm from './components/UserForm.vue';
import ChangePassword from './components/ChangePassword.vue';
import CreateBid from './components/CreateBid.vue';
import axios from 'axios';

axios.interceptors.request.use(
  (config) => {
    const token = store.state.token;  // Получаем токен из Vuex
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;  // Добавляем токен в заголовок
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);
// Создаем маршруты
const routes = [
  { path: '/', component: HomePage },
  { path: '/login', component: LoginPage },
  { path: '/profile', component: ProfilePage },
  { path: '/profile/password', component: ChangePassword },
  { path: '/admin/users', component: UserManagement },
  { path: '/admin/users/create', component: UserForm },
  { path: '/admin/users/:id/edit', component: UserForm, props: true },
  { path: '/create-bid', component: CreateBid},
  // другие маршруты...
];

// Создаем экземпляр маршрутизатора
const router = createRouter({
  history: createWebHistory(),
  routes,
});

// Создаем приложение и подключаем маршрутизатор
const app = createApp(App);
app.use(store);  // Подключаем Vuex в приложение
app.use(router);
app.mount('#app');
