import { createStore } from 'vuex';
import axios from 'axios';


export default createStore({
  state: {
    token: localStorage.getItem('access_token') || '',
  },
  mutations: {
    clearToken(state) {
      state.token = null;  // Удаляем токен
    },
    setToken(state, token) {
      state.token = token;
      localStorage.setItem('access_token', token);
    },
    logout(state) {
      state.token = '';
      localStorage.removeItem('access_token');
    }
  },
  actions: {
    login({ commit }, { token }) {
      commit('setToken', token);
    },
    logout({ commit }) {
      commit('logout');
    },
    // Проверка токена при запуске приложения
    checkToken({ commit }) {
      const token = localStorage.getItem('access_token');
      if (token) {
        axios
          .get('/api/validate_token', { headers: { Authorization: `Bearer ${token}` } })
          .then((response) => {
            if (response.data.valid) {
              // Токен валиден, можно загрузить данные пользователя
            } else {
              commit('logout'); // Если токен невалиден, выходим
            }
          })
          .catch(() => {
            commit('logout'); // Ошибка на сервере или при проверке токена
          });
      } else {
        commit('logout'); // Нет токена - выходим
      }
    }
  },
  getters: {
    getToken(state) {
      return state.token;
    },
    isAuthenticated(state) {
      return !!state.token;
    }
  }
});
