import { createStore } from 'vuex';

export default createStore({
  state: {
    // Получаем токен из localStorage, если он существует
    token: localStorage.getItem('access_token') || '', 
    // Проверяем, что в localStorage есть данные перед тем, как их парсить
    user: localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')) : {} 
  },
  mutations: {
    setToken(state, token) {
      state.token = token;
      localStorage.setItem('access_token', token);  // Сохраняем токен в localStorage
    },
    setUser(state, user) {
      state.user = user;
      localStorage.setItem('user', JSON.stringify(user));  // Сохраняем пользователя в localStorage
    },
    logout(state) {
      state.token = '';
      state.user = {};
      localStorage.removeItem('access_token');  // Удаляем токен из localStorage
      localStorage.removeItem('user');  // Удаляем пользователя из localStorage
    }
  },
  actions: {
    login({ commit }, { token, user }) {
      commit('setToken', token);
      commit('setUser', user);
    },
    logout({ commit }) {
      commit('logout');
    }
  },
  getters: {
    getToken(state) {
      return state.token;
    },
    getUser(state) {
      return state.user;
    },
    isAuthenticated(state) {
      return !!state.token;  // Если токен есть, считаем пользователя авторизованным
    }
  }
});
